package com.Pattern_Problems_9_for_Beginners;

public class Half_Pyramid03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=4;

        for(int i=1; i<=n; i++){
            for(int j=1; j<=i; j++){
                System.out.print("*");

            }
            System.out.println();
        }
	}

}
